import React from 'react'

function Card(props){
    const [affichage, symbol] = React.useState(0);


    return <button type ="button" onClick={turnCard}>
        <div className='Card'>{props.affichage ? props.symbol : '-'}</div>
        </button>;

}

turnCard = () => {
    this.setState({affichage : !affichage})
}




export default Card;
